function GetEpisode(PodcastID, setListaEpisodios, setListaNoticias){
    fetch(`https://api.spreaker.com/v2/shows/` +
                        PodcastID +
                        `/episodes`
    )
    .then((response)=>response.json())	
    .then((episodes) => {
        return episodes;
    })
    .then((episodes)=>{


        if(episodes.response.items[1].show_id != '4119263'){
            let quantidade_episodios = episodes.response.items.length
            var lista_episodio=[];
            for(var i=0; i < quantidade_episodios; i++){

                fetch(`https://api.spreaker.com/v2/episodes/`+episodes.response.items[i].episode_id)
                    .then((response)=>response.json())
                    .then((episodio)=>{
                        lista_episodio.push(episodio.response.episode)
                    })
              }
            setListaEpisodios(lista_episodio)
            console.log("Estou em episodios normais:", lista_episodio)
        }  
        else{
            let quantidade_noticias = episodes.response.items.length
            var lista_noticias=[];
            for(var i=0; i < quantidade_noticias; i++){

                fetch(`https://api.spreaker.com/v2/episodes/`+episodes.response.items[i].episode_id)
                    .then((response)=>response.json())
                    .then((episodio)=>{
                        lista_noticias.push(episodio.response.episode)
                    })
              }
            setListaNoticias(lista_noticias)
            console.log("Estou em episodios de noticias:", lista_noticias)
          }

    })
}

export default GetEpisode
