import {
  React,
  Component,
  useEffect,
  useState
} from "react";

import GetSpreaker from "../src/getSpreaker.js"
import GetEpisode from "../src/getEpisode.js"
import GetNoticias from "../src/getNoticias.js"


function Main() {
  const [podcast, setPodcast] = useState([])
  const [dados, setDados] = useState([])
  const [userID, setUserID] = useState([])
  const [listaProgramas, setListaProgramas] = useState([])
  const [programaNoticia, setProgramaNoticia] = useState([])
  const [listaEpisodios, setListaEpisodios] = useState([])
  const [listaNoticias, setListaNoticias] = useState([])

  useEffect(() => {
    GetSpreaker(setDados, setPodcast, setListaProgramas, setProgramaNoticia)
  }, []);


  function getImages(){


    const listaProgramasFiltrada = listaProgramas.sort((a, b) => b.datacao_filtrada - a.datacao_filtrada)


    let content = []
    for (let el in listaProgramasFiltrada){
      content.push(<img onClick={() => GetEpisode(listaProgramasFiltrada[el].show_id, setListaEpisodios)}
        alt="Avatar" src={listaProgramasFiltrada[el].image_url} width={176} height={180}/>)

    }
    if(programaNoticia[0] !== undefined){ 
        content.push(<img onClick={()=> GetNoticias(setListaNoticias)}
        alt="Avatar" src={programaNoticia[0].image_url}/>) 

    }

    return content


   }




  if (dados !== []) {
    return (
      <div>
        <img style={{}} src="fundo_broadcast.png"/>


        <p>Resultados para: {dados.fullname}</p>
       {dados.image_url !== undefined &&(
        getImages()

       )}

      </div>
    )

    }
}

export default Main;
