function GetEpisode(PodcastID, setListaEpisodios){
    var lista_episodio=[];
    fetch(`https://api.spreaker.com/v2/shows/` +
                        PodcastID +
                        `/episodes?limit=5` /* ESTÁ LIMITANDO PARA OS 5 PRIMEIROS EPISODIOS*/
    )
    .then((response)=>response.json())	
    .then((episodes) => {
        lista_episodio.push({ episode_id: episodio.response.episode.episode_id, title: episodio.response.episode.title, datacao_filtrada: new Date(episodio.response.episode.published_at.split(' ')[0]), published_at: episodio.response.episode.published_at, descricao_episodio: episodio.response.episode.description}
        )
        //console.log(episodes.response)
        setListaEpisodios(lista_episodio)
        return episodes;
    })



    .then((episodes)=>{
        let quantidade_episodios = episodes.response.items.length

        for(var i=0; i < quantidade_episodios; i++){
            fetch(`https://api.spreaker.com/v2/episodes/`+episodes.response.items[i].episode_id)
            .then((response)=>response.json())
            .then((episodio)=>{
                lista_episodio.push({ episode_id: episodio.response.episode.episode_id, title: episodio.response.episode.title, datacao_filtrada: new Date(episodio.response.episode.published_at.split(' ')[0]), published_at: episodio.response.episode.published_at, descricao_episodio: episodio.response.episode.description}
                )

            })      
        }    
        const listaEpisodiosFiltrados = lista_episodio.sort((a, b) => b.datacao_filtrada - a.datacao_filtrada)
        console.log(listaEpisodiosFiltrados)

        setListaEpisodios(lista_episodio)
    })
}

export default GetEpisode
