function GetNoticias(setListaNoticias){
    const PodcastID = '4119263'
    fetch(`https://api.spreaker.com/v2/shows/` +
                        PodcastID +
                        `/episodes`
    )
    .then((response)=>response.json())	
    .then((episodes) => {

        return episodes;
    })
    .then((episodes)=>{

        let quantidade_noticias = episodes.response.items.length
        var lista_noticias=[];
        for(var i=0; i < quantidade_noticias; i++){
            fetch(`https://api.spreaker.com/v2/episodes/`+episodes.response.items[i].episode_id)
                .then((response)=>response.json())
                .then((episodio)=>{
                    lista_noticias.push({episodio: episodio.response.episode, datacao_filtrada: new Date(podcasts.response.items[i].last_episode_at.split(' ')[0]),}
                    )
                })
          }
          setListaNoticias(lista_noticias)
    })
}

export default GetNoticias
