function GetEpisode(PodcastID, setListaEpisodios){
    var lista_episodios=[];
    var lista_episodios_descritos = [];
    var tamanho_lista_episodios = [];
    fetch(`https://api.spreaker.com/v2/shows/` +
                        PodcastID +
                        `/episodes?limit=5` /* ESTÁ LIMITANDO PARA OS 5 PRIMEIROS EPISODIOS*/
    )
    .then((response)=>response.json())	
    .then((episodes) => {
        lista_episodios.push(episodes.response.items)
        tamanho_lista_episodios.push(episodes.response.items.length)
        //console.log(lista_episodios[0])
        return episodes;
    })



    .then((episodes)=>{
        let quantidade_episodios = episodes.response.items.length

        for(var i=0; i < quantidade_episodios; i++){
            fetch(`https://api.spreaker.com/v2/episodes/`+episodes.response.items[i].episode_id)
            .then((response)=>response.json())
            .then((episodio)=>{
                lista_episodios_descritos.push({ episode_id: episodio.response.episode.episode_id, descricao_episodio: episodio.response.episode.description}
                )

            })      
        }    
    })
    console.log(tamanho_lista_episodios[0])
   for(var i=0; i < tamanho_lista_episodios; i++){
       console.log(lista_episodios_descritos[i])
   }


}

export default GetEpisode
