import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

export function CalendarioDiario(conteudo_prismic) {
  console.log("FUNCAO CALENDARIO", conteudo_prismic)
  return (

    <Container fluid style={{backgroundColor:'#E5E5E5', padding:'10px'}}>
      <div style={{textAlign:'center', color: 'black'}}>
        Diário
        </div>
        <br></br>
    <Row xs={2} style={{alignItems:'center'}}>
    <Col style={{width:'10%'}}>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{width:'112px', height:'96px', padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: '#CFCBCB', borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> 
      </Col>

    })}
    </Col>
    <Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{width:'1208px', height:'96px', padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.descricao_programa[0].text}</p> 
      </Col>

    })}</Col>
    </Row>


    </Container>
  );
}



export default CalendarioDiario;
