import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";

export function CalendarioSemanal(conteudo_prismic) {
  //console.log("FUNCAO CALENDARIO", conteudo_prismic)
  const [diaSemana, setDiaSemana] = useState([])
  const [dateTime, setDateTime] = useState([])
  var offset = 0;
  useEffect(() => {
    Dia(setDiaSemana, setDateTime)
  }, []);
  function Dia(setDiaSemana, setDateTime){
    var today = new Date();
    var date =today.getDate()+'/'+(today.getMonth()+1);
    var dateTime = date
    var options = {  weekday: 'long', year: 'numeric', month: 'numeric', day: 'numeric'};
    var prnDt = new Date().toLocaleTimeString('pt-br', options);
    console.log(new Date().toLocaleTimeString('pt-br', options))
    console.log("date",dateTime)
    const dia_mes_semana = prnDt
    const dia_da_semana = dia_mes_semana.toString().split(',')
    setDateTime(dateTime)
    if(dia_da_semana[0] === 'segunda-feira'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira)
    }
    if(dia_da_semana[0] === 'terça-feira'){
     setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.terca_feira)
    }
    if(dia_da_semana[0] === 'quarta-feira'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.quarta_feira)

    }
    if(dia_da_semana[0] === 'quinta-feira'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.quinta_feira)

    }
    if(dia_da_semana[0] === 'sexta-feira'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.sexta_feira)

    }
    if(dia_da_semana[0] === 'sabado'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.sabado)

    }
    if(dia_da_semana[0] === 'domingo'){
      setDiaSemana(conteudo_prismic.conteudo_calendario.results[0].data.domingo)

    }

  }

  function getWeek(offset) {
    offset = offset || 0; 
    var firstDay = new Date();
    firstDay.setDate(firstDay.getDate() - dayOfWeekOffset + (offset * 7));


    var lastDay = new Date(firstDay);
    lastDay.setDate(lastDay.getDate() + 6);

    console.log(makeDateString(firstDay), makeDateString(lastDay));
    range.innerHTML = makeDateString(firstDay) + ' à ' + makeDateString(lastDay);
  }

  function makeDateString(date) {
    var dd = date.getDate();
    var mm = date.getMonth() + 1;
    var y = date.getFullYear();

    var dateString = dd + '/'+ mm + '/'+ y;
    return dateString;

  }

  function backward() {
    offset = offset - 1;
    getWeek(offset);
  }

  function forward() {
    offset = offset + 1;
    getWeek(offset);
  }
  return (

    <Container fluid style={{backgroundColor:'#E5E5E5', padding:'10px'}}>
       <Button style={{padding:'5px', marginLeft:'10px'}} onClick={()=> window.location.replace('/')}>Semanal</Button>
        <Button style={{padding:'5px', marginLeft:'10px'}} onClick={()=>window.location.replace('/cal_diario')}>Diário</Button>
      <div style={{textAlign:'center', color: 'black'}}>

      <button>Cu&lt;</button>
        {dateTime}
        <button>&gt;</button>
        </div>
        <br></br>
    <Row xs="auto">

    <Col>
    <Col>
    <div id="dia_semana" style={{width:'178.3px',backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'8px', textAlign:'center'}}>
    Segunda
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{width:'178.3px', height:'96px', padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'8px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    </Col>

    <Col>
    <Col>
    <div id="dia_semana" style={{width:'178.3px',backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'8px', textAlign:'center'}}>
    Terça
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.terca_feira.map(conteudo=>{
      return <Col style={{width:'178.3px', height:'96px', padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'8px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    </Col>

