import { React, Component, useEffect, useState } from "react";

function Main(){

  const [podcast, setPodcast] = useState([])
  const [dados, setDados] = useState([])
  const [userID, setUserID] = useState([])
  const [listaProgramas, setListaProgramas] = useState([])

  useEffect(() => {
  }, []);


  function search(){
    let chave_acesso = "20e75b947a1909b99b242724438ed46dad9576b1";
      fetch(
        `https://api.spreaker.com/v2/me?oauth2_access_token=` + chave_acesso
      )
        .then((response) => response.json())
        .then((data) => {
          setDados(data.response.user)
          /* this.setState({
            data: data.response.user,
          }); */
          return data;
        })

        .then((data) => {
          if (data.response.user.user_id != undefined) {
