import {
  React,
  Component,
  useEffect,
  useState
} from "react";

import GetSpreaker from "../src/getSpreaker.js"
import GetEpisode from "../src/getEpisode.js"




function Main() {
  const [podcast, setPodcast] = useState([])
  const [dados, setDados] = useState([])
  const [userID, setUserID] = useState([])
  const [listaProgramas, setListaProgramas] = useState([])
  const [programaNoticia, setProgramaNoticia] = useState([])
  const [listaEpisodios, setListaEpisodios] = useState([])
  const [listaNoticias, setListaNoticias] = useState([])

  useEffect(() => {
    GetSpreaker(setDados, setPodcast, setListaProgramas, setProgramaNoticia)
    GetEpisode(4119263, setListaNoticias, 0)
  }, []);



  function teste(lista_noticias){
    lista_noticias = lista_noticias
    console.log(lista_noticias)
    document.getElementById('iframe').src = "https://widget.spreaker.com/player?show_id="  + lista_noticias.episodio_id 

  }



  function getImages(){

    let content = []


    for (let el in listaProgramas){
      content.push(<img onClick={() => GetEpisode(listaProgramas[el].show_id, 0,setListaEpisodios)}
      alt="Avatar" src={listaProgramas[el].image_url} width={176} height={180}/>)
    }

    if(programaNoticia[0]!= undefined){
      for (let el in listaProgramas){
        console.log(listaProgramas[el])
      }
    }

    if(programaNoticia[0]!= undefined){
      content.push(<img 
      alt="Avatar" src={programaNoticia[0].image_url} width={176} height={180}/>)
    }

    for(let el in listaNoticias){
      content.push(<div>{listaNoticias[el].titulo}<span>{listaNoticias[el].descricao}</span><button onClick={()=>teste(listaNoticias[el])}>Play</button><br></br><iframe id="iframe" src="https://widget.spreaker.com/player?=43826958" width="100%" height="200px" frameborder="0"></iframe><br></br></div>)
    }
    return content


   }


  if (listaNoticias !== [] && listaProgramas !== undefined ) {
    return (
      <div>
        <img style={{}} src="fundo_broadcast.png"/>


        <p>Resultados para: {dados.fullname}</p>
       {dados.image_url !== undefined &&(
        getImages()

       )}



      </div>
    )

    }
}

export default Main;
