import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

export function Calendario(conteudo_prismic) {
  console.log("FUNCAO CALENDARIO", conteudo_prismic)
  return (

    <Container style={{backgroundColor:'#E5E5E5'}}>
    <Row>
    <Col>
    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px'}}>
    Segunda
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p>{conteudo.titulo_programa[0].text} {conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    </Col>
    <Col>
    Terça
    </Col>
    <Col>
    Quarta
    </Col>
    <Col>Quinta</Col>
    <Col>Sexta</Col>
    <Col>Sábado</Col>
    <Col>Domingo</Col>
    </Row>
    </Container>
  );
}



export default Calendario;
