import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";

export function CalendarioDiario(conteudo_prismic) {
  console.log("FUNCAO CALENDARIO", conteudo_prismic)
  return (

    <Container fluid style={{backgroundColor:'#E5E5E5', padding:'10px'}}>
      <Button style={{padding:'5px', marginLeft:'10px'}} onClick={()=> window.location.replace('/')}>Semanal</Button>
        <Button style={{padding:'5px', marginLeft:'10px'}} onClick={()=>window.location.replace('/cal_diario')}>Diário</Button>
      <div style={{textAlign:'center', color: 'black'}}>
        Diário
        </div>
        <br></br>
    <Row xs={2}>
    <Col style={{width:'8.5%'}}>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      const titulo_com_horario = conteudo.titulo_programa[0].text
      const horario = titulo_com_horario.toString().split('-')
      return <Col style={{width:'112px', height:'96px',padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: '#CFCBCB', borderRadius:'8px', border:'1px solid #858585'}}>
      <p style={{fontWeight:'bolder', textAlign:'center',padding:'25px'}}>{horario[0]}
      </p> 
      </Col>

    })}
    </Col>
    <Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{width:'1208px', height:'96px', padding:'5px',fontSize:'10px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'8px', border:'1px solid #858585'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.descricao_programa[0].text}</p> 
      </Col>

    })}</Col>
    </Row>


    </Container>
  );
}



export default CalendarioDiario;
