function GetEpisode(PodcastID, setlistaNoticia){
    fetch(`https://api.spreaker.com/v2/shows/` +
                        PodcastID +
                        `/episodes`
    )
    .then((response)=>response.json())	
    .then((episodes) => {
        var i = 0;
        if ( i != 1){
            setlistaNoticia(episodes.response)
            console.log(episodes)
            i ++
            return episodes;
        }
        setlistaNoticia(episodes.response)
        return episodes;
       /*console.log(episodes.response)*/
    })
}

export default GetEpisode
