  search = event => {
    let chave_acesso = '20e75b947a1909b99b242724438ed46dad9576b1'



      fetch(`https://api.spreaker.com/v2/me?oauth2_access_token=`+ chave_acesso)
        .then(response => response.json())
        .then(data => {
          this.setState({
            data: data.response.user
          })
         return data
        })
        .then(data=>{
          console.log(data)
          if(data.user_id != undefined){
            fetch(`https://api.spreaker.com/v2/users/`+ data.user_id+ `/shows`)
              .then(response => response.json())
              .then(podcasts =>{
                this.setState({
                  podcasts: podcasts.response.items
                })
                return podcasts
              })
          .then(podcasts=>{
            console.log(podcasts)
          })
          }

        })





      const{data, podcasts} = this.state
      let user_id = data.user_id



    /*console.log(podcasts)  
    console.log(podcasts[0])*/

    let tamanho = podcasts.length

    var lista_programas = [];
    for (var i = 0; i < tamanho; i++) {

      if(podcasts[i].title != "Rádio UFRJ - Informação & Conhecimento"){
        if(podcasts[i].last_episode_at != null){
          lista_programas.push({show_id: podcasts[i].show_id, title: podcasts[i].title, datacao: podcasts[i].last_episode_at, image_url: podcasts[i].image_url});
        }
      }
    }
    console.log(lista_programas)
    tamanho = lista_programas.length




    return <div>{lista_programas}</div>;





  }








  render() {
    const {data, podcasts} = this.state



    return (
      <div>

        <button onClick={this.search} >Search</button>


        {data && <p>Resultados para: {data.fullname}</p>}

        <img alt="Avatar" src={data.image_url} width={50} />



      </div>
    )
  }
}

export default Search
