import React, { Component, useEffect } from 'react'

class Search extends Component {

  state = {
    ip_user: '',
    data: [],
    eps: []
  }

  search = event => {
    let chave_acesso = '20e75b947a1909b99b242724438ed46dad9576b1'
    let ip_user = ip_user

    setTimeout(() => {

      fetch(`https://api.spreaker.com/v2/me?oauth2_access_token=`+ chave_acesso)
        .then(response => response.json())
        .then(data => {
          this.setState({
            data: data.response.user,
            ip_user: data.response.user.user_id      
          })
        })
        .then(fetch(`https://api.spreaker.com/v2/users/`+ ip_user `/shows`)
          .then(res => res.json())
          .then(eps =>{
            this.setState({
              eps: eps.res
            })
          })
        )
    }, 200)




  }

  render() {
    const {data, eps} = this.state
    console.log(data)
    console.log(eps)

    return (
      <div>

        <button onClick={this.search} >Search</button>


        {data && <p>Resultados para: {data.fullname}</p>}
        <img alt="Avatar" src={data.image_url} width={50} />



      </div>
    )
  }
}

export default Search
