import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

export function Calendario(conteudo_prismic) {
  console.log("FUNCAO CALENDARIO", conteudo_prismic)
  return (

    <Container style={{backgroundColor:'#E5E5E5', padding:'10px'}}>
    <Row>
    <Col>
    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Segunda
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    </Col>
    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Terça
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Quarta
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}

    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Quinta
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}

    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Sexta
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}

    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Sábado
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}

    <Col>
    <div id="dia_semana" style={{backgroundColor:'#D9D8D8', border:'1px solid #858585', borderRadius:'4px', textAlign:'center'}}>
    Domingo
    </div>
    </Col>
    {conteudo_prismic.conteudo_calendario.results[0].data.segunda_feira.map(conteudo=>{
      return <Col style={{padding:'5px',fontSize:'15px', marginTop:'10px',backgroundColor: conteudo.cor_programa, borderRadius:'4px'}}>
      <p style={{fontWeight:'bolder'}}>{conteudo.titulo_programa[0].text}</p> <p>{conteudo.descricao_programa[0].text}</p>
      </Col>
    })}
    </Row>
    </Container>
  );
}



export default Calendario;
