import {
  React,
  Component,
  useEffect,
  useState
} from "react";

import GetSpreaker from "../src/getSpreaker.js"
import GetEpisode from "../src/getEpisode.js"



import { Helmet } from 'react-helmet'

import Row from '../components/Row'
import Live from '../components/Live'
import News from '../components/News'
import Programas from '../components/Programas'

import style from '../assets/css'

import { getTitle } from '../utils/misc'

import logoSrc from '../assets/img/logo.png'
import styled from 'styled-components'




function Main() {
  const [podcast, setPodcast] = useState([])
  const [dados, setDados] = useState([])
  const [userID, setUserID] = useState([])
  const [listaProgramas, setListaProgramas] = useState([])
  const [programaNoticia, setProgramaNoticia] = useState([])
  const [listaEpisodios, setListaEpisodios] = useState([])

  useEffect(() => {
    GetSpreaker(setDados, setPodcast, setListaProgramas, setProgramaNoticia)
  }, []);

  function getImages(){


    const listaProgramasFiltrada = listaProgramas.sort((a, b) => b.datacao_filtrada - a.datacao_filtrada)

    let content = []
    for (let el in listaProgramasFiltrada){
      content.push(<img onClick={() => GetEpisode(listaProgramasFiltrada[el].show_id, setListaEpisodios)}
        alt="Avatar" src={listaProgramasFiltrada[el].image_url} width={176} height={180}/>)
    }

    if(programaNoticia[0] !== undefined){ 
      for (let el in programaNoticia){
        content.push(<img onLoad={() => GetEpisode(programaNoticia[el].show_id, setListaEpisodios)}  
        alt="Avatar" src={programaNoticia[el].image_url}/>)
        }
      console.log(listaEpisodios) 
    }



    return content


   }

  if (dados !== []) {
    /*return (
      <div>
        <img style={{}} src="fundo_broadcast.png"/>
        <p>Resultados para: {dados.fullname}</p>
       {dados.image_url !== undefined &&(
        getImages()

       )}

      </div>
    )*/
    const { app } = style.components
   return (
      <div
         className="overflow-x"
         style={{ padding: 'var(--padding) var(--padding) 0' }}
      >
         <Helmet>
            <title>{getTitle()}</title>
            <meta
               name="description"
               content="Rádio institucional da Universidade Federal do Rio de Janeiro"
            />
         </Helmet>
         <MediaQuery maxWidth={800}>
            <img className={app.logo} src={logoSrc} alt="" />
         </MediaQuery>
         <Row>
            <Live />
            <MediaQuery minWidth={701}>
               <WrapperScrollerAside>
                  <News breakPoint={701} />
               </WrapperScrollerAside>
            </MediaQuery>
         </Row>
         <MediaQuery maxWidth={700}>
            <Row>
               <News />
            </Row>
         </MediaQuery>
         <Row>
            <Programas />
         </Row>
      </div>
   )
    }
}

export default Main;
