  handleSearch = event => {
    const query = event.target.value

    setTimeout(() => {
      fetch(`https://api.spreaker.com/v2/me?oauth2_access_token=20e75b947a1909b99b242724438ed46dad9576b1`)
        .then(response => response.json())
        .then(data => {
          console.log(data);
          this.setState({
            data: data
          })
        })
    }, 200)
  }

  render() {
    const {data } = this.state

