import React, { Component, useEffect } from 'react'

class Search extends Component {

  state = {
    user_id: [],
    data: [],
    podcasts: []
  }

  /*constructor(props) {
    super(props);
    this.handleAuthorChange = this.handleAuthorChange.bind(this);
  } 

  handleAuthorChange(event) {
    let {name: fieldName, value} = event.target;

    this.setState({
      [fieldName]: value
    });
  };*/


  search = event => {
    let chave_acesso = '20e75b947a1909b99b242724438ed46dad9576b1'


    setTimeout(() => {


      fetch(`https://api.spreaker.com/v2/me?oauth2_access_token=`+ chave_acesso)
        .then(response => response.json())
        .then(data => {
          this.setState({
            data: data.response.user
          })

        }) 

      }, 200)

    setTimeout(()=>{
      const{data, podcasts} = this.state
      let user_id = data.user_id

      if(user_id != undefined){
        fetch(`https://api.spreaker.com/v2/users/`+ user_id+ `/shows`)
          .then(response => response.json())
          .then(podcasts =>{
            this.setState({
              podcasts: podcasts.response.items
            })
          })
           console.log(podcasts)
      }

    /*console.log(podcasts)  
    console.log(podcasts[0])*/

    let tamanho = podcasts.length

    var lista_programas = [];
    for (var i = 0; i < tamanho; i++) {

      if(podcasts[i].title != "Rádio UFRJ - Informação & Conhecimento"){
        if(podcasts[i].last_episode_at != null){
          lista_programas.push({show_id: podcasts[i].show_id, title: podcasts[i].title, last_episode_at: podcasts[i].last_episode_at, image_url: podcasts[i].image_url});
        }
      }
      console.log(lista_programas)
    }







    return <div>{lista_programas}</div>;


    }, 200)


  }








  render() {
    const {data, podcasts} = this.state



    return (
      <div>

        <button onClick={this.search} >Search</button>


        {data && <p>Resultados para: {data.fullname}</p>}

        <img alt="Avatar" src={data.image_url} width={50} />



      </div>
    )
  }
}

export default Search
