import {
  React,
  Component,
  useEffect,
  useState
} from "react";

import GetSpreaker from "../src/getSpreaker.js"
import GetEpisode from "../src/getEpisode.js"





function Main() {
  const [podcast, setPodcast] = useState([])
  const [dados, setDados] = useState([])
  const [userID, setUserID] = useState([])
  const [listaProgramas, setListaProgramas] = useState([])
  const [programaNoticia, setProgramaNoticia] = useState([])
  const [listaEpisodios, setListaEpisodios] = useState([])

  const [descricaoEpisodio, setDescricaoEpisodio] = useState([])

  useEffect(() => {
    GetSpreaker(setDados, setPodcast, setListaProgramas, setProgramaNoticia)
    GetEpisode(4119263, setListaEpisodios)

  }, []);



  function cria_player(lista_ordenada_episodios){
    lista_ordenada_episodios = lista_ordenada_episodios
    const iframe = document.createElement('iframe');
    const html = "https://widget.spreaker.com/player?episode_id="  + lista_ordenada_episodios.episode_id + "&hide-logo=true"; 
    /*No link https://developers.spreaker.com/guides/embedding-the-player/ ele mostra as opções que existem no player, como mudaça para o tema dark, autoplay e 
    coisas do genero. Para implementar tais opções, só é necessario adicionar após a variável de ID do episodio ou do show um &... 
    Como está acima, &theme=dark ativa o tema dark, se fosse uma opção como hide-logo seria &hide-logo=true. */ 
    iframe.src = html;
    iframe.sandbox = 'allow-scripts allow-popups';
    iframe.width = "50%";
    iframe.height="100px";
    iframe.frameborder="0";
    document.body.appendChild(iframe);
  }


  function getImages(){

    let content = []





    for (let el in listaProgramas){
      console.log(listaProgramas)
      content.push(<img onClick={() => GetEpisode(listaProgramas[el].show_id, setListaEpisodios)}
      alt="Avatar" src={listaProgramas[el].image_url} width={176} height={180}/>)
    }



    if(listaProgramas[0]!= undefined){
      content.push(<img 
      alt="Avatar" src={programaNoticia[0].image_url} width={176} height={180}/>)
    }

    for(let el in listaEpisodios){
      //console.log(listaEpisodios[el])
      for(let ul in listaEpisodios[el]){
        content.push(<div>{listaEpisodios[el][ul].title+ "."}<span></span><br></br><button onClick={()=>cria_player(listaEpisodios[el][ul])}>Play</button><br></br><br></br></div>)
      }
    }
    return content


   }



    return (
      <div>
        <img style={{}} src="fundo_broadcast.png"/>


        <p>Resultados para: {dados.fullname}</p>
       {getImages()}



      </div>
    )


}

export default Main;
