import React, { Component, useEffect } from 'react'

class Search extends Component {

  state = {
    data: []
  }

  search = event => {


    setTimeout(() => {
      fetch(`https://api.spreaker.com/v2/me?oauth2_access_token=20e75b947a1909b99b242724438ed46dad9576b1`)
        .then(response => response.json())
        .then(data => {
          console.log(data);
          this.setState({
            data: data.response.user      
          })
        })
    }, 200)


  //useEffect

  }

  render() {
    const {data} = this.state
    console.log(data)
    return (
      <div>

        <button onClick={this.search} >Search</button>


        {data && <p>Resultados para: {data.fullname}</p>}
        <img alt="Avatar" src={data.image_url} width={50} />



      </div>
    )
  }
}

export default Search
